CREATE TABLE users(
    id int not null auto_increment primary key,
    name varchar(50) not null,
    email varchar(50) not null,
    password text null,
    _created datetime default current_timestamp);

CREATE TABLE chirps(
    id int not null auto_increment primary key,
    user_id int,
    text text null,
    location varchar(50),
    _created datetime default current_timestamp);

ALTER TABLE chirps
    ADD CONSTRAINT fk_user_chirps
    FOREIGN KEY (user_id)
    REFERENCES users(id);

